<?php$user 	= new ControllUser();if (!$user->get_session()){   header("location:logout.php");}$konten 	= new ControllKonten();$pesan 		= "";if ($_SERVER["REQUEST_METHOD"] == "POST") {		$id_konten		= $_POST['id_konten'];	$judul			= $_POST['judul'];	$klik			= $_POST['klik'];	$lokasi_file    = $_FILES['fupload']['tmp_name'];	$tipe_file      = $_FILES['fupload']['type'];	$nama_file      = $_FILES['fupload']['name'];	$acak           = rand(1,99);	$nama_file_unik = $acak.$nama_file; 		$sekarang		= gmdate("Y-m-d H:i:s", time()+60*60*7);	$tanggal_update	= gmdate("Y-m-d H:i:s", time()+60*60*7);		//dapatkan url web dari kelas about	$about = new ControllAbout();	$url_web = $about->getURL();		//direktori file text	$vdir_upload = "../file/";	$vfile_upload = $vdir_upload . $nama_file_unik;	if (empty($lokasi_file)){		$upload = FALSE;	}	else{		 //Simpan file fdi folder file		$upload = move_uploaded_file($lokasi_file, $vfile_upload);	}	 //proses simpan data	if ($upload)	{			//dapatkan isi text		$url_file = $url_web."file/".$nama_file_unik;		//$data_file 	= file_get_contents($url_file);						$ch = curl_init();		curl_setopt ($ch, CURLOPT_URL, $url_file);		curl_setopt ($ch, CURLOPT_HEADER, 0);		ob_start();		curl_exec ($ch);		curl_close ($ch);		$data_file = ob_get_contents();		ob_end_clean();				//buat instance kelas konten		$konten->setKonten($id_konten,$sekarang,$judul,$data_file,$url_file,$nama_file_unik,$tanggal_update,$klik);		$update_konten = $konten->updateKonten();	}	else	{		//buat instance kelas konten		$data_file = '';		$url_file = '';		$konten->setKonten($id_konten,$sekarang,$judul,$data_file,$url_file,$nama_file_unik,$tanggal_update,$klik);		$update_konten = $konten->updateKontenNotFile();	}		if ($update_konten)	{		$pesan = "<font color=green>Update Data Berhasil</font>";	}	else	{		$pesan =  "<font color=red>Update Data Gagal</font>";	}	}	//dapatkan data konten	$id 			= $_GET['id'];	$sekarang		= '';	$judul			= '';	$data_file		= '';	$url_file		= '';	$nama_file_unik	= '';	$tanggal_update	= '';	$klik			= '';	$konten->setKonten($id,$sekarang,$judul,$data_file,$url_file,$nama_file_unik,$tanggal_update,$klik);	$data_konten 	= $konten->getKonten();			foreach ($data_konten as $value)	{		extract ($value);				}	?><fieldset><center><?php echo "$pesan"; ?></center></fieldset><ul id="breadcrumb">        <li><a href="#" title="Home"><img src="images/home.png" alt="Home" class="home" /></a></li>        <li>Import Data Text</li></ul><fieldset><form action="" method="POST" enctype="multipart/form-data"><input type="hidden" value="<?php echo $id ?>" name="id_konten"><input type="hidden" value="<?php echo $klik ?>" name="klik"><table>	<tr>		<td>Judul</td>		<td><input type="text" name="judul" value="<?php echo $judul_konten ?>" size="50"/></td>	</tr>	<tr>		<td>File Text Baru</td>		<td><input type="file" name="fupload" />&nbsp;*) kosongkan jika tidak ingin diganti</td>	</tr>	<tr>		<td>File Lama</td>		<td><a href="<?php echo $url_file ?>" target="_blank">Download</a></td>	</tr>	<tr>		<td colspan="2" align="center"><input type="submit" value="Ubah Data Text" class="button"/></td>	</tr></table></form></fieldset>