<?php$user		= new ControllUser();if (!$user->get_session()){   header("location:index.php");}$kelas_link = new ControllLink();if ($_SERVER["REQUEST_METHOD"] == "POST") {	$id 		= $_POST['id'];	$link		= $_POST['link'];	$status 	= $_POST['status'];	$crawled 	= $_POST['crawled'];	    $kelas_link->setLink($id,$link,$status,$crawled);    $update 	= $kelas_link->updateLink();	if ($update) 	{		echo "<fieldset>";        echo "<center><font color=green> Sukses Update </font><br>";		echo "<input type=\"button\" value=\"Kembali\" class=button onClick=\"self.history.back();\"><center>";		echo "</fieldset><br/>";          } 	else	{			echo "<fieldset>";        echo "<center><font color=red>[Error] Gagal Update</font><br>";		echo "<input type=\"button\" value=\"Kembali\" class=button onClick=\"self.history.back();\"><center>";		echo "</fieldset><br/>";        	}}?><script language="javascript">	function cekInputUser()	{		var con=true;		var error="Kesalahan Pengisian : \n";		var code = 0;				if (document.input_user.link.value=="" || document.input_user.link.value==" ")		{			error += "Silakan Masukan Link!\n";			code=1;			con=false;		}				if (document.input_user.email.value=="" || document.input_user.email.value==" ")		{			error += "Silakan Masukan Email!\n";			code=1;			con=false;		}				if (code==1)		{			alert (error);			return con;		}	}</script><ul id="breadcrumb">        <li><a href="#" title="Home"><img src="images/home.png" alt="Home" class="home" /></a></li>        <li><a href="#" title="Data Link">Data Link</a></li>        <li>Edit Data Link</li>    </ul><?php	//dapatkan data admin	$id 		= $_GET['id'];	$link		= '';	$status		= '';	$crawled	= '';	$kelas_link->setLink($id,$link,$status,$crawled);	$data_link 	= $kelas_link->getLink($id);			foreach ($data_link as $value)	{		extract ($value);				}		?><form name="input_user" method="post" action="" onSubmit="return cekInputUser();"><input type="hidden" 