<?php$user = new ControllUser();if (!$user->get_session()){   header("location:index.php");}if ($_SERVER["REQUEST_METHOD"] == "POST") {	$uid		= '';	$username	= $_POST['username'];	$password	= md5($_POST['password']);	$nama 		= $_POST['nama'];	$email		= $_POST['email'];		$user->setUser($uid,$username,$password,$nama,$email);    $insert = $user->insertUser();     if ($insert) 	{		echo "<fieldset>";        echo "<center><font color=green> Sukses Simpan Data </font><br>";		echo "<input type=\"button\" value=\"Kembali\" class=button onClick=\"self.history.back();\"><center>";		echo "</fieldset><br/>";          } 	else	{			echo "<fieldset>";        echo "<center><font color=red>[Error] Gagal Simpan Data</font><br>";		echo "<input type=\"button\" value=\"Kembali\" class=button onClick=\"self.history.back();\"><center>";		echo "</fieldset><br/>";        	}}?><script language="javascript">	function cekInputUser()	{		var con=true;		var error="Kesalahan Pengisian : \n";		var code = 0;				if (document.input_user.nama.value=="" || document.input_user.nama.value==" ")		{			error += "Silakan Masukan Nama Admin!\n";			code=1;			con=false;		}				if (document.input_user.username.value=="" || document.input_user.username.value==" ")		{			error += "Silakan Masukan Username!\n";			code=1;			con=false;		}				if (document.input_user.password.value=="" || document.input_user.password.value==" ")		{			error += "Silakan Masukan Password!\n";			code=1;			con=false;		}				if (document.input_user.email.value=="" || document.input_user.email.value==" ")		{			error += "Silakan Masukan Email!\n";			code=1;			con=false;		}				if (code==1)		{			alert (error);			return con;		}	}</script><ul id="breadcrumb">        <li><a href="#" title="Home"><img src="images/home.png" alt="Home" class="home" /></a></li>        <li><a href="#" title="Data User">Data User</a></li>        <li>Input Data User</li>    </ul><form name="input_user" method="post" action="" onSubmit="return cekInputUser();"><table align="center"><tr>	<td>Nama Admin</td>    <td>:</td>    <td><input type="text" size="30" name="nama"></td></tr><tr>	<td>Username</td>    <td>:</td>    <td><input type="text" size="30" name="username"></td></tr><tr>	<td>Password</td>    <td>:</td>    <td><input type="password" size="30" name="password"></td></tr><tr>	<td>Email</td>    <td>:</td>    <td><input type="text" size="30" name="email"></td></tr><tr>	<td colspan="3" align="center"><input type="submit" value="Simpan" class="button">&nbsp;<input type="button" value="Kembali" class=button onClick="self.history.back();"></td>	</tr></table></form>